import React from 'react'
import ShowPatron from './ShowPatron'

class Result extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      stag:[5, 2, 3, 1],
      phoenix:[3, 5, 1, 2],
      wolf:[5, 1, 3, 2],
      cat:[4, 2, 3, 5],
      horse:[5, 3, 2, 1],

      results: {
        stag: '',
        phoenix: '',
        wolf: '',
        cat: '',
        horse: ''
      }
    }
    this.renderResults = this.renderResults.bind(this)
  }

  renderResults() {
    let {stag, phoenix, wolf, cat, horse} = NaN
    const userScore = this.props.userScore
    const reducer = (accumulator, currentValue) => accumulator + currentValue

    stag = this.state.stag.map(function(item, index) {
        return Math.abs(item - userScore[index])
    })
    phoenix = this.state.phoenix.map(function(item, index) {
      return Math.abs(item - userScore[index])
    })
    wolf = this.state.wolf.map(function(item, index) {
      return Math.abs(item - userScore[index])  
    })
    cat = this.state.cat.map(function(item, index) {
    return Math.abs(item - userScore[index])
    })
    horse = this.state.horse.map(function(item, index) {
    return Math.abs(item - userScore[index])
    })
    this.setState.results({
      stag: stag.reduce(reducer),
      phoenix: phoenix.reduce(reducer),
      wolf: wolf.reduce(reducer),
      cat: cat.reduce(reducer),
      horse: horse.reduce(reducer),
    })

        // return Math.min.apply( Math, results );

  }

  render() {
    return(
      <div>
        <h3>
          Honesty: {this.props.userScore[0]} <br/>
          Mean: {this.props.userScore[1]} <br/> 
          Sports: {this.props.userScore[2]} <br/>
          Money: {this.props.userScore[3]} <br/>
        </h3>
        <h3>Your Patronous score is: {!this.renderResults() ? '' : this.renderResults()}</h3>
        <p>{this.state.results}</p>
        <ShowPatron renderResult={this.renderResult} />
      </div>
    )
  }
}

export default Result
