import React from 'react'
import Form from './Form'

class FormData extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      firstName: "",
      lastName: ""
    }
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event) {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  }
}


export default FormData
